    public static String createTransforms(TestCaseMessage testCaseMessage) {
        String transforms = "";
        BufferedReader inTransform = new BufferedReader(new StringReader(testCaseMessage.getCauseIssues()));
        String line;
        try {
            while ((line = inTransform.readLine()) != null) {
                line = line.trim();
                boolean not = false;
                if (line.startsWith("NOT ")) {
                    not = true;
                    line = line.substring(4).trim();
                }
                transforms = IssueCreatorHeader.createTransforms(testCaseMessage, line, transforms, not);
                transforms = IssueCreatorNextOfKin.createTransforms(testCaseMessage, line, transforms, not);
                transforms = IssueCreatorObservation.createTransforms(testCaseMessage, line, transforms, not);
                transforms = IssueCreatorPatient.createTransforms(testCaseMessage, line, transforms, not);
                transforms = IssueCreatorVaccination.createTransforms(testCaseMessage, line, transforms, not);
            }
        } catch (IOException ioe) {
            throw new IllegalArgumentException("Error reading string", ioe);
        }
        return transforms;
    }
